import React, { useEffect, useState } from "react";
import { Pie } from "react-chartjs-2";
import "chart.js/auto";

function App() {
  const [expenses, setExpenses] = useState([]);
  const [income, setIncome] = useState([]);
  const [text, setText] = useState("");
  const [amount, setAmount] = useState("");
  const [type, setType] = useState("expense");

  const API_URL = "http://localhost:5000/api";

  useEffect(() => {
    fetchExpenses();
    fetchIncome();
  }, []);

  const fetchExpenses = async () => {
    const res = await fetch(`${API_URL}/expenses`);
    const data = await res.json();
    setExpenses(data);
  };

  const fetchIncome = async () => {
    const res = await fetch(`${API_URL}/income`);
    const data = await res.json();
    setIncome(data);
  };

  const addTransaction = async (e) => {
    e.preventDefault();

    if (!text || !amount) return;

    const body = { text, amount: Number(amount) };

    await fetch(
      type === "expense" ? `${API_URL}/expenses` : `${API_URL}/income`,
      {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      }
    );

    setText("");
    setAmount("");

    fetchExpenses();
    fetchIncome();
  };

  const deleteExpense = async (id) => {
    await fetch(`${API_URL}/expenses/${id}`, { method: "DELETE" });
    fetchExpenses();
  };

  const deleteIncome = async (id) => {
    await fetch(`${API_URL}/income/${id}`, { method: "DELETE" });
    fetchIncome();
  };

  const totalExpense = expenses.reduce((acc, cur) => acc + cur.amount, 0);
  const totalIncome = income.reduce((acc, cur) => acc + cur.amount, 0);
  const balance = totalIncome - totalExpense;

  const pieData = {
    labels: expenses.map((e) => e.text),
    datasets: [
      {
        data: expenses.map((e) => e.amount),
        backgroundColor: [
          "#ff6384",
          "#36a2eb",
          "#ffcd56",
          "#4bc0c0",
          "#9966ff",
          "#ff9f40",
        ],
      },
    ],
  };

  return (
    <div className="container">
      <h1>Expense & Income Tracker</h1>

      <div className="summary">
        <div className="card income">Income: ₹{totalIncome}</div>
        <div className="card expense">Expense: ₹{totalExpense}</div>
        <div className="card balance">Balance: ₹{balance}</div>
      </div>

      <form onSubmit={addTransaction} className="form">
        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="expense">Expense</option>
          <option value="income">Income</option>
        </select>

        <input
          type="text"
          placeholder="Title"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />

        <input
          type="number"
          placeholder="Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />

        <button type="submit">Add</button>
      </form>

      <div className="lists">
        <div>
          <h3>Expenses</h3>
          {expenses.map((e) => (
            <div className="item" key={e._id}>
              {e.text} — ₹{e.amount}
              <button onClick={() => deleteExpense(e._id)}>X</button>
            </div>
          ))}
        </div>

        <div>
          <h3>Income</h3>
          {income.map((i) => (
            <div className="item" key={i._id}>
              {i.text} — ₹{i.amount}
              <button onClick={() => deleteIncome(i._id)}>X</button>
            </div>
          ))}
        </div>
      </div>

      <div className="chartBox">
        <h3>Spending Breakdown</h3>
        <Pie data={pieData} />
      </div>
    </div>
  );
}

export default App;
