import { useEffect, useState } from "react";
import axios from "axios";
import { FaTrash, FaPlus } from "react-icons/fa";
import { Doughnut } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";
ChartJS.register(ArcElement, Tooltip, Legend);

// APIs
const EXP_API = "http://localhost:5000/api/expenses";
const INC_API = "http://localhost:5000/api/income";

function App() {
  const [expenses, setExpenses] = useState([]);
  const [income, setIncome] = useState([]);

  // expense form
  const [title, setTitle] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("Food");

  // income form
  const [incomeTitle, setIncomeTitle] = useState("");
  const [incomeAmount, setIncomeAmount] = useState("");

  // -------- FETCH --------
  const fetchExpenses = async () => {
    const res = await axios.get(EXP_API);
    setExpenses(res.data);
  };

  const fetchIncome = async () => {
    const res = await axios.get(INC_API);
    setIncome(res.data);
  };

  useEffect(() => {
    fetchExpenses();
    fetchIncome();
  }, []);

  // -------- ADD EXPENSE --------
  const addExpense = async (e) => {
    e.preventDefault();
    if (!title || !amount) return;
    await axios.post(EXP_API, { title, amount: +amount, category });
    setTitle("");
    setAmount("");
    fetchExpenses();
  };

  // -------- ADD INCOME --------
  const addIncome = async (e) => {
    e.preventDefault();
    if (!incomeTitle || !incomeAmount) return;
    await axios.post(INC_API, { title: incomeTitle, amount: +incomeAmount });
    setIncomeTitle("");
    setIncomeAmount("");
    fetchIncome();
  };

  // -------- DELETE EXPENSE --------
  const deleteExpense = async (id) => {
    await axios.delete(`${EXP_API}/${id}`);
    fetchExpenses();
  };

  // -------- TOTALS --------
  const totalExpense = expenses.reduce((a, e) => a + e.amount, 0);
  const totalIncome = income.reduce((a, i) => a + i.amount, 0);
  const balance = totalIncome - totalExpense;

  // -------- CHART --------
  const totals = expenses.reduce((acc, e) => {
    acc[e.category] = (acc[e.category] || 0) + e.amount;
    return acc;
  }, {});

  const chartData = {
    labels: Object.keys(totals),
    datasets: [
      {
        data: Object.values(totals),
        backgroundColor: [
          "#ff6384",
          "#36a2eb",
          "#ffcd56",
          "#4bc0c0",
          "#9966ff",
          "#ff9f40"
        ],
        borderWidth: 0
      }
    ]
  };

  return (
    <div className="app">
      <h1>ðŸ’° Expense Tracker</h1>

      {/* SUMMARY */}
      <div className="summary">
        <div className="card incomeCard">Income: â‚¹{totalIncome}</div>
        <div className="card expenseCard">Expense: â‚¹{totalExpense}</div>
        <div className="card balanceCard">Balance: â‚¹{balance}</div>
      </div>

      {/* ===== INCOME FORM ===== */}
      <form className="card" onSubmit={addIncome}>
        <h3>Add Income</h3>
        <input
          placeholder="Income Title"
          value={incomeTitle}
          onChange={(e) => setIncomeTitle(e.target.value)}
        />

        <input
          type="number"
          placeholder="Amount â‚¹"
          value={incomeAmount}
          onChange={(e) => setIncomeAmount(e.target.value)}
        />

        <button className="add">
          <FaPlus /> Add Income
        </button>
      </form>

      {/* ===== EXPENSE FORM ===== */}
      <form className="card" onSubmit={addExpense}>
        <h3>Add Expense</h3>

        <input
          placeholder="Expense Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />

        <input
          type="number"
          placeholder="Amount â‚¹"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />

        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option>Food</option>
          <option>Shopping</option>
          <option>Transport</option>
          <option>Entertainment</option>
          <option>Other</option>
        </select>

        <button className="add">
          <FaPlus /> Add Expense
        </button>
      </form>

      {/* ===== GRID ===== */}
      <div className="grid">
        {/* LIST */}
        <div className="card list">
          <h3>Expenses</h3>
          {expenses.map((e) => (
            <div key={e._id} className="row">
              <span>{e.title}</span>
              <span>â‚¹{e.amount}</span>
              <span className="cat">{e.category}</span>
              <FaTrash onClick={() => deleteExpense(e._id)} />
            </div>
          ))}
        </div>

        {/* CHART */}
        <div className="card chart">
          <h3>Spending Breakdown</h3>
          <Doughnut data={chartData} />
        </div>
      </div>
    </div>
  );
}

export default App;
